import React, { useState } from 'react';
import Board from './Board';
import Timer from './Timer';
import useTimer from '../hooks/useTimer';
import calculateWinner from '../utils/calculateWinner';

const Game: React.FC = () => {
    const [history, setHistory] = useState([Array(9).fill(null)]);
    const [stepNumber, setStepNumber] = useState(0);
    const [xIsNext, setXIsNext] = useState(true);
    const [ascending, setAscending] = useState(true);  // 정렬 상태
  
    const currentSquares = history[stepNumber];
    const winner = calculateWinner(current);
  
    // 타이머 설정: 각 플레이어에게 10초 제한 부여
    const { timeLeft, resetTimer } = useTimer(10, handleTimeUp);

    const handleTimeUp = () => {
        if (xIsNext) {
            handleClick([...currentSquares]);
        }
    }

    const handleClick = (i: number) => {
      const newHistory = history.slice(0, stepNumber + 1);
      const current = newHistory[newHistory.length - 1];
      const squares = current.slice();
  
      if (winner || squares[i]) {
        return;
      }
  
      squares[i] = xIsNext ? 'X' : 'O';
      setHistory(newHistory.concat([squares]));
      setStepNumber(newHistory.length);
      setXIsNext(!xIsNext);
    };
  
    const jumpTo = (step: number) => {
      setStepNumber(step);
      setXIsNext(step % 2 === 0);
      resetTimer();
    };
  
    const renderMoves = () => {
      const moves = history.map((_, move) => {
        const desc = move
          ? `Go to move #${move} (${Math.floor((move - 1) / 3)}, ${(move - 1) % 3})`
          : 'Go to game start';
  
        return (
          <li key={move}>
            <button onClick={() => jumpTo(move)}>{desc}</button>
          </li>
        );
      });
  
      return ascending ? moves : moves.reverse();  // 정렬된 목록 반환
    };
  
    return (
      <div className="game">
        <div className="game-board">
          <Board squares={currentSquares} onSquareClick={handleClick} winningSquares={winner ? winner.winningSquares : []}/>
          <Timer timeLeft={timeLeft} />
        </div>
        <div className="game-info">
          <div>{winner ? `Winner: ${winner.winner}` : stepNumber === 9 ? 'Draw!' : `Next player: ${xIsNext ? 'X' : 'O'}`}</div>
          <button onClick={() => setAscending(!ascending)}>
            Sort by: {ascending ? 'Ascending' : 'Descending'}
          </button>
          <ol>{renderMoves()}</ol>
        </div>
      </div>
    );
  };  

export default Game;
