import React, { useState } from 'react';
import Board from './Board';
import calculateWinner from '../utils/calculateWinner';

const Game: React.FC = () => {
    const [history, setHistory] = useState([Array(9).fill(null)]);
    const [stepNumber, setStepNumber] = useState(0);
    const [xIsNext, setXIsNext] = useState(true);
  
    const current = history[stepNumber];
    const result = calculateWinner(current);  // calculateWinner는 승자와 승리 칸 배열을 반환
    const winner = result ? result.winner : null;
    const winningSquares = result ? result.winningSquares : null;
  
    const handleClick = (i: number) => {
      const newHistory = history.slice(0, stepNumber + 1);
      const current = newHistory[newHistory.length - 1];
      const squares = current.slice();
  
      if (winner || squares[i]) {
        return;
      }
  
      squares[i] = xIsNext ? 'X' : 'O';
      setHistory(newHistory.concat([squares]));
      setStepNumber(newHistory.length);
      setXIsNext(!xIsNext);
    };
  
    return (
      <div className="game">
        <div className="game-board">
          <Board
            squares={current}
            onSquareClick={handleClick}
            winningSquares={winningSquares}  // 승리 칸 전달
          />
        </div>
        <div className="game-info">
          <div>{winner ? `Winner: ${winner}` : `Next player: ${xIsNext ? 'X' : 'O'}`}</div>
        </div>
      </div>
    );
  };  

export default Game;
